public static byte[] saveBase64strToFile(String base64str){
    if (base64str == null){
        return null;
    }
    try{
	byte[] decodeBase64 = Base64.decodeBase64(base64str);	 
    return decodeBase64;        
    } 
    catch (Exception e){
       e.printStackTrace();
    }
    return null;
}

public static byte[] saveBase64strToFile1(String base64str){
    if (base64str == null){
        return null;
    }
    try{
	byte[] decodeBase64 = net.iharder.Base64.decode(base64str.getBytes());	
    return decodeBase64;        
    } 
    catch (Exception e){
       e.printStackTrace();
    }
    return null;
}


